const tabItems=document.querySelectorAll(".tab__item"),tabContentItems=document.querySelectorAll(".tab-content__item");function selectItem(e){removeBorder(),removeshow(),this.classList.add("tab--selected"),document.querySelector(`#${this.id}-content`).classList.add("show-tab-content")}function removeBorder(){tabItems.forEach(e=>e.classList.remove("tab--selected"))}function removeshow(){tabContentItems.forEach(e=>e.classList.remove("show-tab-content"))}tabItems.forEach(e=>e.addEventListener("click",selectItem));